mapper.select:
SELECT

FROM tb_adss a
    LEFT JOIN tb_ban_type bt ON a.ban_type_id = bt.ban_type_code
    LEFT JOIN tb_city ci ON a.city_id = ci.city_code
    LEFT JOIN tb_colour co ON a.colour_id = co.colour_code
    LEFT JOIN tb_currency cu ON a.currency_id = cu.currency_code
    LEFT JOIN tb_dest_measure dm ON a.dest_measure_id = dm.dest_code
    LEFT JOIN tb_fuel_type ft ON a.fuel_type_id = ft.fuel_type_code
    LEFT JOIN tb_gear_box_type gbt ON a.gear_box_type_id = gbt.gear_box_code
    LEFT JOIN tb_gear_type gt ON a.gear_type_id = gt.gear_type_code
    LEFT JOIN tb_model m ON a.model_id = m.model_code
    LEFT JOIN tb_brand b ON m.brand_id = b.brand_code


    a.is_active = 1


        AND a.model_id IN

             #{mid}



        AND m.brand_id =#{request.brand}


    AND a.used = #{request.used}

        AND a.city_id IN

            #{cid}


    AND a.price >= #{request.priceMin}
    AND #{request.priceMax} >= a.price
    AND a.currency_id = #{request.currency}

        AND a.ban_type_id IN

            #{btid}


    AND a.prod_year >= #{request.yearMin}
    AND #{request.yearMax} >= a.prod_year

        AND a.colour_id IN

            #{coid}



        AND a.fuel_type_id IN

            #{ftid}



        AND a.gear_box_type_id IN

            #{gbtid}



        AND a.gear_type_id IN

            #{gtid}



        AND a.engine_power >= #{request.enginePowerMin}


        AND #{request.enginePowerMax} >= a.engine_power


        AND a.engine_horse_power >= #{request.horsePowerMin}


        AND #{request.horsePowerMax} >= a.engine_horse_power


        AND a.destination >= #{request.destinationMin}


        AND #{request.destinationMax} >= a.destination


        AND a.dest_measure_id = #{request.destinationMeasure}


        AND a.number_of_seats IN

            #{nosid}




ORDER BY a.ttl
DESC LIMIT #{limit}
OFFSET #{from};

SELECT COUNT(a.ads_link)
FROM tb_adss a
WHERE a.ads_link = #{adsLink, jdbcType=NVARCHAR};

mapper.update:
UPDATE allinonedb.tb_adss
SET IS_ACTIVE = 0
WHERE ADS_LINK = #{adsLink, jdbcType=NVARCHAR};
