mapper.select:
SELECT 
FROM tb_adss a
LEFT JOIN tb_ban_type bt ON a.ban_type_id = bt.ban_type_code
LEFT JOIN tb_city ci ON a.city_id = ci.city_code
LEFT JOIN tb_colour co ON a.colour_id = co.colour_code
LEFT JOIN tb_currency cu ON a.currency_id = cu.currency_code
LEFT JOIN tb_dest_measure dm ON a.dest_measure_id = dm.dest_code
LEFT JOIN tb_fuel_type ft ON a.fuel_type_id = ft.fuel_type_code
LEFT JOIN tb_gear_box_type gbt ON a.gear_box_type_id = gbt.gear_box_code
LEFT JOIN tb_gear_type gt ON a.gear_type_id = gt.gear_type_code
LEFT JOIN tb_model m ON a.model_id = m.model_code
LEFT JOIN tb_brand b ON m.brand_id = b.brand_code


    1 = 1

     AND a.model_id = #{model} 
     AND m.brand_id = #{brand} 

     AND a.used = #{used} 
     AND a.city_id = #{city} 
     AND a.price >= #{priceMin} 
     AND #{priceMax} >= a.price 
     AND a.currency_id = #{currency} 
     AND a.ban_type_id = #{banType} 
     AND a.prod_year >= #{yearMin} 
     AND #{yearMax} >= a.prod_year 
     AND a.colour_id = #{colour} 
     AND a.fuel_type_id = #{fuelType} 
     AND a.gear_box_type_id = #{gearBoxType} 
     AND a.gear_type_id = #{gearType} 
     AND a.engine_power >= #{enginePowerMin} 
     AND #{enginePowerMax} >= a.engine_power 
     AND a.engine_horse_power >= #{horsePowerMin} 
     AND #{horsePowerMax} >= a.engine_horse_power 
     AND a.destination >= #{destinationMin} 
     AND #{destinationMax} >= a.destination 
     AND a.dest_measure_id = #{destinationMeasure} 
     AND a.number_of_seats = #{numberOfSeats} 


ORDER BY ttl DESC LIMIT #{size} OFFSET #{page};

SELECT COUNT(a.ads_link)
FROM tb_adss a
WHERE a.ads_link = #{adsLink, jdbcType=NVARCHAR};
